<?php namespace App\Models; use Illuminate\Database\Eloquent\Factories\HasFactory; use Illuminate\Database\Eloquent\Model; class PuntoGOB extends Model { use HasFactory; protected $table = 'punto_gobs'; protected $fillable = ['name', 'location', 'capacity', 'status',]; public function users() { return $this->hasMany(User::class, 'punto_gob_id'); } public function institutions() { return $this->belongsToMany(Institution::class, 'institution_punto_gob'); } }